sys_ux_client_script.required_translations:
[ {
  "code" : null,
  "message" : "You are now logged out of your session",
  "comment" : ""
} ]

sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    if (api.context.props.logout == 'true') {
        helpers.translate('You are now logged out of your session').then((message) => {
            api.emit("NOW_UXF_PAGE#ADD_NOTIFICATIONS", {
                items: [{
                    id: "alert1",
                    status: "info",
                    icon: "circle-check-outline",
                    header: "",
                    content: {
                        type: "html",
                        value: `<span>${message}</span>`,
                    },
                    action: {
                        type: "dismiss"
                    },
                }],
            });
        });
    }
}
