sys_ux_data_broker_transform.required_translations:
[ {
  "message" : "Graphql Query Output",
  "comment" : ""
}, {
  "message" : "Number on record",
  "comment" : ""
}, {
  "message" : "SysID of the logged in user",
  "comment" : ""
} ]

sys_ux_data_broker_transform.script:
function transform(input){
	try{
		var user_id = input.user;
		var record_id = input.number;
		var target = new GlideRecord("sysapproval_approver");
		target.addQuery("sysapproval",record_id);
		target.addQuery("approver", user_id);
		target.query(); 
		if (target.next()) { 
			return target.getUniqueValue();
		}
	}catch(e){}
}
